import React from "react";
import { keyframes } from "styled-components";

const baseURL = "https://strangers-things.herokuapp.com/api/2105-SJS-RM-WEB-PT";

const Messages = ({ user, token }) => {
  const userMessages = user.messages;
  console.log(userMessages);

  return (
    <div className='prev-msgs'>
          <h1 className='prev-msgs-head'>Messages Received</h1>
          {
            userMessages.map(message => {
              return <>
              {user._id !== message.fromUser._id ?
               <div className='prev-msg'>
                     <h1>Item: {message.post.title}</h1>
                   <h3>Message from: {message.fromUser.username}</h3>
                  <h3>User says: {message.content}</h3>
                </div>
              : ''}
              </>
            })
          }
          <h1 className='prev-msgs-head'>Messages Sent</h1>
          {
            userMessages.map(message => {
              return <>
              {user._id === message.fromUser._id ?
               <div className='prev-msg'>
                     <h1>Item: {message.post.title}</h1>
                 <h3>Message from: {message.fromUser.username}</h3>
                   <h3>User says: {message.content}</h3>
                </div>
              : ''}
              </>
            })
          }
        </div>
  )
}

export default Messages;
